const reducer = (state,action) => {
    switch (action.type) {
     case 'get_dentists':
         return{...state, dentistas: action.payload}   
     case 'change_style':
         return{...state, theme:action.payload}         
     default:
         break;
    }
 
 }
 export default reducer
